SELECT goods_id as goodsId, goods_name as goodsName, goods_price as goodsPrice, goods_pic as goodsPic, house_id as houseId, is_one as isOne, product_data as productData, house_type as houseType, lottery_price as lotteryPrice FROM goods tbl where 1=1
and house_id = #{houseId}
and is_one = #{isOne}
limit ${pageNow},${pageSize}

SELECT COUNT(1) FROM goods
where 1=1

    and is_one = #{isOne}
